
public class Conta {
	
	String nome;
	double valor;


	public Conta(String nome, double valor) {
		this.nome = nome;
		this.valor = valor;
	}


	@Override
	public String toString() {
		// TODO Auto-generated method stub
		return "Conta [nome=" + nome + ", valor=" + valor + "]";
		
		
	}

	
	
	
}
